@Component({
  selector: 'app-cursos',
  templateUrl: './cursos.component.html',
  styleUrls: ['./cursos.component.css']
})
export class CursosComponent {
  pessoas = [
    {nome: 'josé'},
    {nome: 'maria'},
    {nome: 'grazi'},
    {nome: 'fernanda'},
    {nome: 'robsom'},
    {nome: 'enrique'},
    {nome: 'juliana'},
    {nome: 'paula'},
    {nome: 'renata'},
    {nome: 'lucas'},
  ];
}
