import { Component } from '@angular/core';

@Component({
  selector: 'app-headder',
  templateUrl: './headder.component.html',
  styleUrls: ['./headder.component.css']
})
export class HeadderComponent {
  infoProjeto = [
  {nome: 'DOCERIA',descrisao: 'Feito com tecnicas de HTML5, CSS3, Flex, pronta para adição de novos recursos'},
  ]
}
<nav>
    <a routerLink="">Home</a>
    <a routerLink="cursos">Cursos</a>
    <a routerLink="habilidades">Habilidades</a>  
        <details>
            <summary>Projetos</summary>
            <a routerLink="doceria">Doceria</a>
            <a routerLink="sitecomercial">Site comercial</a>
            <a routerLink="webcurriculo">WebCurriculo</a>
            <a routerLink="portifolio">Portifolio</a>        
        </details>
</nav>
